              :label=\"option.name\"
              :disabled=\"!option.stock || option.stock <= 0\"
            >
              {{ option.name }} 
              <span v-if=\"option.price > 0\">+¥{{ option.price }}</span>
              <span v-if=\"!option.stock || option.stock <= 0\" class=\"stock-out\">缺货</span>
