<template><div class="app-container"><Navbar /><main class="main-content"><div class="product-main"><ProductGallery :images="productImages" /><ProductInfo :product="product" @quantity-change="updateQuantity" @spec-change="updateSpec" /></div><ProductTabs><template #tab-content><ProductSpecifications :specifications="product.specifications" /></template><template #tab-scenarios><UsageScenarios :scenarios="product.scenarios" /></template><template #tab-reviews><UserReviews :reviews="product.reviews" /></template></ProductTabs><div class="bottom-actions"><AfterSalesService :services="afterSalesServices" /><AddToCartSection :quantity="quantity" :selected-spec="selectedSpec"@update-quantity="updateQuantity"@add-to-cart="addToCart"@buy-now="buyNow" /></div></main></div></template><script setup>import Navbar from './components/Navbar.vue'import ProductGallery from './components/ProductGallery.vue'import ProductInfo from './components/ProductInfo.vue'import ProductTabs from './components/ProductTabs.vue'import ProductSpecifications from './components/ProductSpecifications.vue'import UsageScenarios from './components/UsageScenarios.vue'import UserReviews from './components/UserReviews.vue'import AddToCartSection from './components/AddToCartSection.vue'import AfterSalesService from './components/AfterSalesService.vue'import { ref, reactive } from 'vue'const product = reactive({name: "智能手表 Pro Max",price: 1299,originalPrice: 1599,description: "全新智能手表 Pro Max，搭载最新健康监测系统，支持100+运动模式，超长续航，IP68防水",images: ["https://picsum.photos/id/1/800/800","https://picsum.photos/id/2/800/800","https://picsum.photos/id/3/800/800","https://picsum.photos/id/4/800/800"],specifications: [{ name: "屏幕尺寸", value: "1.78英寸 AMOLED" },{ name: "处理器", value: "双核处理器" },{ name: "内存", value: "1GB RAM + 16GB ROM" },{ name: "电池容量", value: "380mAh" },{ name: "续航时间", value: "常规使用7天，重度使用3天" },{ name: "防水等级", value: "IP68" },{ name: "蓝牙版本", value: "蓝牙5.2" },{ name: "传感器", value: "心率、血氧、睡眠、运动传感器" },{ name: "系统要求", valu