<template><div class="product-info"><h1 class="product-title">{{ product.name }}</h1><p class="product-description">{{ product.description }}</p><div class="price-section"><span class="current-price">¥{{ selectedSpec.price.toLocaleString() }}</span><span class="original-price" v-if="product.originalPrice">¥{{ product.originalPrice.toLocaleString() }}</span></div><div class="spec-section"><h3>选择规格</h3><div class="spec-options"><div v-for="spec in product.specs" :key="spec.id":class="['spec-option', selectedSpec.id === spec.id ? 'active' : '']"@click="selectSpec(spec)"><span>{{ spec.name }}</span><span class="price-tag" v-if="spec.price !== product.price">¥{{ spec.price.toLocaleString() }}</span></div></div><div class="stock-info"><i class="el-icon-info"></i><span>库存: {{ selectedSpec.stock }}件</span></div></div></div></template><script setup>import { defineProps, defineEmits, ref, watch } from 'vue'const props = defineProps({product: {type: Object,required: true}})const emits = defineEmits(['spec-change'])const selectedSpec = ref(props.product.specs[0])watch(() => props.product.specs, (newSpecs) => {if (newSpecs && newSpecs.length > 0) {selectedSpec.value = newSpecs[0]}})const selectSpec = (spec) => {selectedSpec.value = specemits('spec-change', spec)}</script><style scoped>.product-info {flex: 1;min-width: 300px;}.product-title {font-size: 24px;color: #333;margin-bottom: 15px;}.product-description {color: #666;line-height: 1.6;margin-bottom: 25px;}.price-section {margin-bottom: 30px;}.current-price {font-size: 28px;color: #e53e3e;font-weight: bold;margin-right: 15px;}.original-price {font-size: 18px;color: #999;text-decoration: line-through;}.spec-section {margin-top: 30px;}.spec-section h3 {font-size: 16px;margin-bottom: 15px;color: #333;}.spec-options {display: flex;flex-wrap: wrap;gap: 10px;margin-bottom: 15px;}.spec-option {padding: 10px 15px;border: 1px solid #ddd;border-radius: 4px;cursor: pointer;transition: all 0.3s;display: flex;align-items: center;gap: 10px;}.spec-option:hover {border-color: #1a73e8;}.spec-option.active {border-color: #1a73e8;background-color: #f0f7ff;color: #1a73e8;font-weight: 500;}.price-tag {color: #e53e3e;font-weight: 500;}.stock-info {display: flex;align-items: center;color: #666;font-size: 14px;margin-top: 10px;}.stock-info i {margin-right: 5px;color: #1a73e8;}</style>