<template><div class="app-container"><Navbar/><main class="main-content"><div class="product-container"><ProductGallery :images="productImages"/><ProductInfo :product="product" @update-quantity="updateQuantity" @update-selected-options="updateSelectedOptions"/></div><ProductTabs :specifications="product.specifications" :usage-scenarios="product.usageScenarios" :reviews="product.reviews"/></main><PurchaseSection :price="product.price" :quantity="quantity" :selected-options="selectedOptions" :stock="product.stock" @update-quantity="updateQuantity" @add-to-cart="addToCart" @buy-now="buyNow"/><AfterSales/></div></template><script setup>import Navbar from './components/Navbar.vue';import ProductGallery from './components/ProductGallery.vue';import ProductInfo from './components/ProductInfo.vue';import ProductTabs from './components/ProductTabs.vue';import PurchaseSection from './components/PurchaseSection.vue';import AfterSales from './components/AfterSales.vue';import { ref, reactive } from 'vue';const quantity = ref(1);const selectedOptions = reactive({color: '', size: ''});const product = reactive({id: 1, name: 'UltraWatch Pro 智能手表',subtitle: '全圆高清屏 | 心率监测 | 50米防水 | 14天续航',price: 1299,originalPrice: 1599,discount: '8.1折',stock: 256,colors: [{id: 'black', name: '曜石黑', image: '/images/colors/black.jpg', stock: 120},{id: 'silver', name: '月光银', image: '/images/colors/silver.jpg', stock: 86},{id: 'gold', name: '玫瑰金', image: '/images/colors/gold.jpg', stock: 50}],sizes: [{id: '42mm', name: '42mm', stock: 135},{id: '46mm', name: '46mm', stock: 121}],specifications: [{category: '基本信息', items: [{name: '品牌', value: 'UltraWatch'},{name: '型号', value: 'Pro-2023'},{name: '上市时间', value: '2023年9月'},{name: '产地', value: '中国深圳'}]},{category: '屏幕信息', items: [{name: '屏幕尺寸', value: '1.78英寸 AMOLED'},{name: '分辨率', value: '368 x 448像素'},{name: '屏幕类型', value: '全圆触摸屏'},{name: '亮度', value: '最高600尼特'}]},{category: '功能参数', items: [{name: '防水等级', value: '50米防水'},{name: '续航时间', value: '普通模式14天，GPS模式25小时'},{name: '传感器', value: '心率传感器、血氧传感器、加速度传感器'},{name: '连接方式', value: '蓝牙5.2, Wi-Fi, NFC'}]},usageScenarios: [{id: 1, image: '/images/scenarios/sports.jpg', title: '运动健身', description: '实时监测运动数据，科学规划健身计划'},{id: 2, image: '/images/scenarios/work.jpg', title: '商务办公', description: '消息提醒，会议预约，提升工作效率'},{id: 3, image: '/images/scenarios/sleep.jpg', title: '睡眠监测', description: '全面分析睡眠质量，改善睡眠习惯'},{id: 4, image: '/images/scenarios/outdoor.jpg', title: '户外探险', description: 'GPS定位，海拔监测，适应各种环境'}],reviews: [{id: 1, user: '张先生', avatar: '/images/avatars/user1.jpg', rating: 5, date: '2023-10-15', content: '手表非常漂亮，功能强大，续航也很给力，充一次电用了12天还有余电。心率监测很准确，运动模式也很多样。', images: ['/images/reviews/review1-1.jpg', '/images/reviews/review1-2.jpg']},{id: 2, user: '李女士', avatar: '/images/avatars/user2.jpg', rating: 4, date: '2023-10-10', content: '外观时尚，佩戴舒适，操作流畅。唯一不足是表带有点硬，戴久了有点不舒服，自己换了个表带就完美了。', images: ['/images/reviews/review2-1.jpg']},{id: 3, user: '王先生', avatar: '/images/avatars/user3.jpg', rating: 5, date: '2023-10-05', content: '性价比很高的一款智能手表，功能齐全，屏幕显示效果出色，阳光下也能清晰可见。客服响应很快，有问必答。', images: []}]});const productImages = ['/images/product/main.jpg','/images/product/side1.jpg','/images/product/side2.jpg','/images/product/features1.jpg','/images/product/features2.jpg'];function updateQuantity(newQuantity) {quantity.value = newQuantity;}function updateSelectedOptions(options) {Object.assign(selectedOptions, options);}function addToCart() {if (!selectedOptions.color || !selectedOptions.size) {alert('请选择产品颜色和尺寸');return;}alert(`已将 ${quantity.value} 件 ${product.name} (${selectedOptions.color}/${selectedOptions.size}) 加入购物车`);}function buyNow() {if (!selectedOptions.color || !selectedOptions.size) {alert('请选择产品颜色和尺寸');return;}alert(`立即购买 ${quantity.value} 件 ${product.name} (${selectedOptions.color}/${selectedOptions.size})，总价: ${product.price * quantity.value}元`);}</script><style scoped>.app-container {min-height: 100vh;display: flex;flex-direction: column;}.main-content {flex: 1;padding: 20px 0;}.product-container {display: flex;gap: 40px;margin-bottom: 40px;}@media (max-width: 768px) {.product-container {flex-direction: column;}.main-content {padding: 10px;}}</style>