<template><div class="product-info"><h1 class="product-name">{{ product.name }}</h1><p class="product-subtitle">{{ product.subtitle }}</p><div class="price-section"><span class="current-price">¥{{ product.price }}</span><span class="original-price">¥{{ product.originalPrice }}</span><span class="discount-tag">{{ product.discount }}</span></div><div class="stock-info"><el-tag type="success">有货</el-tag><span>库存: {{ product.stock }}件</span></div><div class="options-section"><div class="option-group"><h3>颜色</h3><div class="option-items"><div v-for="color in product.colors" :key="color.id" class="color-option" :class="{ 'selected': selectedOptions.color === color.id }" @click="selectOption('color', color.id)"><img :src="color.image" alt="{{ color.name }}" class="color-image"/><span class="color-name">{{ color.name }}</span><span class="color-stock">({{ color.stock }}件)</span></div></div></div><div class="option-group"><h3>尺寸</h3><div class="option-items"><div v-for="size in product.sizes" :key="size.id" class="size-option" :class="{ 'selected': selectedOptions.size === size.id }" @click="selectOption('size', size.id)">{{ size.name }}</div></div></div></div></div></template><script setup>import { defineProps, defineEmits, reactive } from 'vue';const props = defineProps({product: {type: Object,required: true}});const emit = defineEmits(['update-quantity', 'update-selected-options']);const selectedOptions = reactive({color: '',size: ''});function selectOption(type, value) {selectedOptions[type] = value;emit('update-selected-options', {...selectedOptions});}</script><style scoped>.product-info {flex: 1;min-width: 300px;}.product-name {font-size: 28px;margin-bottom: 10px;color: #333;}.product-subtitle {color: #666;margin-bottom: 20px;font-size: 15px;}.price-section {margin-bottom: 20px;display: flex;align-items: center;gap: 15px;}.current-price {font-size: 32px;color: #E63946;font-weight: bold;}.original-price {font-size: 16px;color: #999;text-decoration: line-through;}.discount-tag {background-color: #FF4D4F;color: white;padding: 2px 8px;border-radius: 4px;font-size: 14px;}.stock-info {margin-bottom: 25px;display: flex;align-items: center;gap: 10px;}.options-section {margin-top: 30px;}.option-group {margin-bottom: 25px;}.option-group h3 {font-size: 16px;margin-bottom: 12px;color: #333;}.option-items {display: flex;flex-wrap: wrap;gap: 15px;}.color-option {display: flex;flex-direction: column;align-items: center;padding: 10px;border: 1px solid #ddd;border-radius: 8px;cursor: pointer;transition: all 0.3s;}.color-option.selected {border-color: #165DFF;box-shadow: 0 0 0 2px rgba(22, 93, 255, 0.2);}.color-image {width: 60px;height: 60px;border-radius: 50%;object-fit: cover;margin-bottom: 8px;}.color-name {font-size: 14px;margin-bottom: 4px;}.color-stock {font-size: 12px;color: #999;}.size-option {padding: 12px 25px;border: 1px solid #ddd;border-radius: 8px;cursor: pointer;transition: all 0.3s;font-size: 16px;}.size-option:hover {border-color: #165DFF;}.size-option.selected {border-color: #165DFF;background-color: rgba(22, 93, 255, 0.1);color: #165DFF;font-weight: bold;}</style>