<template><div class="specifications-table"><el-table :data="formattedSpecifications" border style="width: 100%"><el-table-column prop="category" label="类别" width="180"></el-table-column><el-table-column prop="name" label="参数名称"></el-table-column><el-table-column prop="value" label="参数值"></el-table-column></el-table></div></template><script setup>import { defineProps, computed } from 'vue';const props = defineProps({specifications: {type: Array,required: true}});const formattedSpecifications = computed(() => {return props.specifications.flatMap(category =>category.items.map(item => ({category: category.category,name: item.name,value: item.value})));});</script><style scoped>.specifications-table {padding: 20px;}.el-table {font-size: 14px;}.el-table th {background-color: #f5f7fa;}</style>